// src/components/Home.jsx
import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="relative bg-cover bg-center h-screen" style={{ backgroundImage: 'url("https://source.unsplash.com/1600x900/?islamic")' }}>
      <div className="absolute inset-0 bg-gradient-to-r from-green-500 via-transparent to-blue-500 opacity-75"></div>
      <div className="relative z-10 flex flex-col items-center justify-center h-full text-center text-white px-4">
        <h1 className="text-5xl md:text-7xl font-extrabold mb-6 shadow-lg">Welcome to Zakaat Calculator</h1>
        <p className="text-xl md:text-2xl mb-8 shadow-lg">Calculate your Zakaat with ease and accuracy.</p>
        <Link to="/calculator" className="px-6 py-3 bg-yellow-500 text-lg font-semibold rounded-lg hover:bg-yellow-700 transition transform hover:scale-105">
          Get Started
        </Link>
      </div>
    </div>
  );
};

export default Home;
