// src/components/ZakaatCalculator.jsx
import React, { useState, useEffect } from 'react';
import { db, doc, getDoc } from '../firebase';

const ZakaatCalculator = () => {
  const [gold, setGold] = useState('');
  const [silver, setSilver] = useState('');
  const [cash, setCash] = useState('');
  const [liabilities, setLiabilities] = useState('');
  const [result, setResult] = useState('');
  const [goldPrice, setGoldPrice] = useState(57); // Default value

  useEffect(() => {
    // Fetch gold price from Firebase
    const fetchGoldPrice = async () => {
      try {
        const docRef = doc(db, 'prices', 'gold');
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          setGoldPrice(docSnap.data().price);
        } else {
          console.log('No such document!');
        }
      } catch (error) {
        console.error('Error fetching gold price:', error);
      }
    };

    fetchGoldPrice();
  }, []);

  const calculateZakaat = () => {
    const goldValue = parseFloat(gold) || 0;
    const silverValue = parseFloat(silver) || 0;
    const cashValue = parseFloat(cash) || 0;
    const liabilitiesValue = parseFloat(liabilities) || 0;

    const nisabValue = 85 * goldPrice; // Using fetched gold price
    const totalAssets = goldValue + silverValue + cashValue;
    const netAssets = totalAssets - liabilitiesValue;

    if (netAssets >= nisabValue) {
      const zakaat = netAssets * 0.025; // 2.5% of net assets
      setResult(`Zakaat to be paid: ${zakaat.toFixed(2)} currency units`);
    } else {
      setResult('You are not eligible to pay Zakaat.');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h1 className="text-4xl font-bold text-center mb-6 text-blue-600">Zakaat Calculator</h1>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            calculateZakaat();
          }}
          className="space-y-4"
        >
          <div>
            <label className="block text-gray-700">Value of Gold (in currency):</label>
            <input
              type="number"
              value={gold}
              onChange={(e) => setGold(e.target.value)}
              className="mt-1 p-2 w-full border border-gray-300 rounded"
            />
          </div>
          <div>
            <label className="block text-gray-700">Value of Silver (in currency):</label>
            <input
              type="number"
              value={silver}
              onChange={(e) => setSilver(e.target.value)}
              className="mt-1 p-2 w-full border border-gray-300 rounded"
            />
          </div>
          <div>
            <label className="block text-gray-700">Cash in Hand/Bank (in currency):</label>
            <input
              type="number"
              value={cash}
              onChange={(e) => setCash(e.target.value)}
              className="mt-1 p-2 w-full border border-gray-300 rounded"
            />
          </div>
          <div>
            <label className="block text-gray-700">Liabilities (in currency):</label>
            <input
              type="number"
              value={liabilities}
              onChange={(e) => setLiabilities(e.target.value)}
              className="mt-1 p-2 w-full border border-gray-300 rounded"
            />
          </div>
          <button
            type="submit"
            className="w-full py-3 px-4 bg-blue-500 text-white rounded-lg hover:bg-blue-700 transition transform hover:scale-105"
          >
            Calculate Zakaat
          </button>
        </form>
        <h2 className="mt-4 text-gray-700 text-center">{result}</h2>
      </div>
    </div>
  );
};

export default ZakaatCalculator;
