// src/components/ZakaatCalculator.jsx
import React, { useState } from 'react';
import Step1 from './Step1';
import Step2 from './Step2';
import Step3 from './Step3';

const ZakaatCalculator = () => {
  const [step, setStep] = useState(1);
  const [data, setData] = useState({
    gold: '',
    silver: '',
    cash: '',
    receivables: '',
    businessStock: '',
    shares: '',
    pensions: '',
    arrears: '',
    bills: '',
    longTermDebts: ''
  });

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  switch (step) {
    case 1:
      return <Step1 nextStep={nextStep} handleChange={handleChange} data={data} />;
    case 2:
      return <Step2 nextStep={nextStep} prevStep={prevStep} handleChange={handleChange} data={data} />;
    case 3:
      return <Step3 data={data} prevStep={prevStep} />;
    default:
      return <Step1 nextStep={nextStep} handleChange={handleChange} data={data} />;
  }
};

export default ZakaatCalculator;
